/* Loop Code */

/* Big thanks to @rico4dev for providing the base start for ATR calcs
I made some tweaks so now the way ATR is calculated matches TradingView :-)
 */

// Set parameters to be used for ATR Period and Multiplier
let Nperiod = 10 // ATR Period
let multiplier = 3 // ATR Multiplier

// Get Current and Previous Candle Data
let currCandle = record.current 
let prevCandle = record.previous 

/* We need to build the price source
For current price there is no problem, we have data
For previous price we must check if exist
Source price is defined as OHCL4*/

// OHCL4 Source for current price
variable.currClose = currCandle.close
variable.ohcl4 = (currCandle.max + currCandle.min + currCandle.close + currCandle.open)/4

// OHCL4 Source for previous candle price - make sure data exists
if (prevCandle === undefined) {
    variable.prevClose = currCandle.close
    variable.prev_ohcl4 = 0    
} else {
    variable.prevClose = prevCandle.close
    variable.prev_ohcl4 = (prevCandle.max + prevCandle.min + prevCandle.close + prevCandle.open)/4
}

/* Define variables we need and build functions */
/* True Range and ATR - Average True Range */

variable.trueRange = calculateTR()
variable.atrNPeriod = calculateATR(Nperiod)
variable.prevAtrNperiod = variable.atrNPeriod // Store ATR value for calcs in RMA

/* To keep lastNperiodTR array to a fixed length
we remove the first element of the array if length is > Nperiod */

variable.lastNPeriodTR.push(calculateTR())
if (variable.lastNPeriodTR.length > Nperiod) {
    variable.lastNPeriodTR.splice(0, 1)
}

/* Calculate ATR */
function calculateATR(length) {
    return calculateRMA(variable.trueRange, length)
}

/* Calculate True Range */
function calculateTR() {
    let hl = currCandle.max - currCandle.min    
    if (variable.prevClose === 0) { return hl } // Return High-Low when previous Close is 0    
    let hcp = Math.abs(currCandle.max - variable.prevClose)
    let lcp = Math.abs(currCandle.min - variable.prevClose)    
    return Math.max(hl, hcp, lcp)
}

/* Calculate RMA */
function calculateRMA(src, length) {    
    if (src.length < length) { return 0 } // We check we have enough values to make the calcs. If not, set value to zero 
    let alpha=1/length // It's a constant
    if (isNaN(variable.prevAtrNperiod)) {
        return 0    
    } else {
        return alpha * src + (1 - alpha) * variable.prevAtrNperiod            
    }  

}

// Build Up Trend function

variable.up = variable.ohcl4 - (multiplier * variable.atrNPeriod)

if (variable.last2uptrend.length > 2) {
    variable.last2uptrend.splice(0, 1)
}

variable.up1 = variable.last2uptrend[variable.last2uptrend.length - 1] // Previous Up Trend 

variable.uptrend = calculateUpTrend()

variable.last2uptrend.push(variable.uptrend) 

function calculateUpTrend() {
    if (variable.prevClose > variable.up1) {
        return Math.max(variable.up, variable.up1)
    } else {
        return variable.up
    }
}

// Build Down Trend function

variable.dn = variable.ohcl4 + (multiplier * variable.atrNPeriod)

if (variable.last2downtrend.length > 2) {
    variable.last2downtrend.splice(0, 1)
}

variable.dn1 = variable.last2downtrend[variable.last2downtrend.length - 1] // Previous Down Trend

variable.downtrend = calculateDnTrend()

variable.last2downtrend.push(variable.downtrend) 

function calculateDnTrend() {
    if (variable.prevClose < variable.dn1) {
        return Math.min(variable.dn, variable.dn1)
    } else {
        return variable.dn
    }
}


// Build Trend function

if (variable.last2Trend.length > 2) {
    variable.last2Trend.splice(0, 1)
}

variable.prevTrend = variable.last2Trend[variable.last2Trend.length - 1]

variable.trend = calculateTrend()

variable.last2Trend.push(variable.trend)

function calculateTrend() {

    if (variable.trend = -1 && variable.currClose > variable.dn1) {
        return 1
    }
    else if (variable.trend = 1 && variable.currClose < variable.up1) {
        return -1
    }    
    else {
        return variable.prevTrend
    }
}

// End of code :-)

/* Initialization Code*/

//// Arrays to store past data
variable.lastNPeriodTR = []
variable.last2uptrend = [0]
variable.last2downtrend = [0]
variable.last2Trend = [0]

// Initialize trend
variable.trend = 1

/* Loop Code */

let candle = record.current

/* Set constant */

let n1=10 // Channel Length
let n2=21 // Average Length

// Overbought and Oversold Levels
let ob=60
let ob2=53
let os=-60
let os2=-53

/* ************** */

variable.ap = (candle.max+candle.min+candle.close)/3

/* Build 3 EMAs and CI parameter */

variable.EMAesa = calculateEMA(variable.ap, n1, variable.previousEMAesa)
variable.previousEMAesa = variable.EMAesa

variable.EMAd = calculateEMA(Math.abs(variable.ap-variable.EMAesa), n1, variable.previousEMAd)
variable.previousEMAd = variable.EMAd

variable.ci = (variable.ap-variable.EMAesa)/(0.015*variable.EMAd)

variable.EMAtci = calculateEMA(variable.ci, n2, variable.previousEMAtci)
variable.previousEMAtci = variable.EMAtci

function calculateEMA(src, periods, previousEMA) {
    let k = 2 / (periods + 1)
    return src * k + previousEMA * (1-k)
}

/* Let's build WAVETREND and WAVETRENDSIGNAL 
For WAVETRENDSIGNAL we need a SMA - Parameters are SMA(WAVETREND, 4)
*/

variable.waveTrend = variable.EMAtci // Wave Trend - It's a line

variable.last4wt1.push(variable.waveTrend)
if (variable.last4wt1.length > 4) {
    variable.last4wt1.splice(0, 1) // Remove the first element of the array to keep it at a maximun of 4 elements
}

variable.waveTrendSignal = calculateSMA(variable.last4wt1, 4) // Signal - It's a line

//console.log("variable.last4wt1.length " + variable.last4wt1.length)
//console.log("variable.last4wt1 " + variable.last4wt1)


function calculateSMA(src, periods) { 
    // Check we have enough values to make the calculation
    if (src.length < periods) { return 0 }

    let sum = 0 // Initialize sum variable
    for (let i = src.length - periods; i < src.length; i++) {
        sum = sum + src[i]
    }
    let sma = sum / periods
    return sma
}

/* Build WT3 */

variable.wt3 = variable.waveTrend - variable.waveTrendSignal // Histogram

/* Initialization Code */
/* WaveTrend need 3 EMA and 1 SMA
others constant and vars are declared in the loop code
so to use let and not var and avoid possible conflicts */


variable.previousEMAesa = 0
variable.previousEMAd = 0
variable.previousEMAtci = 0

